// page init
jQuery(function(){
	initMobileNav();
	initCycleCarousel();
	initCarousel();
	initSlideShow();
	initSameHeight();
	initTabs();
	initAnchors();
	initLightbox();
	initNavigationSelect();
	initCustomForms();
	jQuery('input, textarea').placeholder();
});

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		menuActiveClass: 'active',
		menuOpener: '.btn-opener',
		hideOnClickOutside: true,
		menuDrop: '.top-panel',
	});
}

// generate select from navigation
function initNavigationSelect() {
	jQuery('#tabs').navigationSelect({
		defaultOptionAttr: 'title'
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false,
		wrapNativeOnMobile: false
	});
	jcf.replaceAll();
}

// smooth anchor links
function initAnchors() {
	/* global SmoothScroll */
	// simple case:
	new SmoothScroll({
		anchorLinks: 'a.go-top'
	});
}

// cycle scroll gallery init
function initCycleCarousel() {
	jQuery('.carousel').scrollAbsoluteGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		stretchSlideToMask: true,
		pauseOnHover: true,
		maskAutoSize: true,
		autoRotation: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

// scroll gallery init
function initCarousel() {
	jQuery('.products-slider, .quotes-slider').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.product, .slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		generatePagination: '.pagination',
		circularRotation: true,
		maskAutoSize: false,
		autoRotation: true,
		switchTime: 3000,
		animSpeed: 700,
		step: 1
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.switcher li',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.carousel').sameHeight({
		elements: '.slide',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
	jQuery('.holder').sameHeight({
		elements: '.col',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').contentTabs({
		autoHeight: true,
		animSpeed: 300,
		tabLinks: 'a'
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').fancybox({
		loop: false,
		helpers: {
			overlay: {
				css: {
					background: 'rgba(0, 0, 0, 0.65)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		}
	});
}

